mapper.select:
SELECT CONTACTID 
FROM user_contact 
WHERE USERNUMBER = #{userNumber} 
AND CONTACT_TITLE = #{contact_title} 
AND CONTACT_CONTENT = #{contact_content}
AND ROWNUM = 1

mapper.update:
UPDATE user_contact 
SET CONTACT_TITLE = #{contact_title}, 
    CONTACT_CONTENT = #{contact_content},
    UPDATED_DATE = SYSDATE
WHERE USERNUMBER = #{userNumber} 
AND CONTACT_TITLE = #{contact_title} 
AND CONTACT_CONTENT = #{contact_content}

UPDATE user_contact 
SET STATUS = 'DELETED',
    DELETED_DATE = SYSDATE
WHERE USERNUMBER = #{userNumber} 
AND CONTACT_TITLE = #{contact_title} 
AND CONTACT_CONTENT = #{contact_content}
